    private void bEqualsMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_bEqualsMouseClicked
        num2 = Double.parseDouble(field.getText());
        
        // calculate
        calculate();
        
        // display answer
        field.setText(Double.toString(answer));
        num1 = 0;
        num2 = 0;
    }//GEN-LAST:event_bEqualsMouseClicked

