    private double num1;
    private double num2;
    private double answer;
    private char sign;
    
    public void calculate(){
        switch(sign){
            case '+':
                answer = num1 + num2;
                break;
            case '-':
                answer = num1 - num2;
                break;
            case '/':
                answer = num1 / num2;
                break;
            case '*':
                answer = num1 * num2;
                break;
            default:
                field.setText("error in sign");
        }
    }
